import React, { useRef, useState } from 'react';
import axios from 'axios';
import * as XLSX from 'xlsx';
import './App.css'; // Importing CSS
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [keyword, setKeyword] = useState('');
  const [city, setCity] = useState('');
  const [tableData, setTableData] = useState(null);
  const tableRef = useRef(null);  // Create a ref
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    setTableData(null);
    setLoading(true); // Show loading screen

    setIsLoading(true);
    try {
      const response = await axios.post('http://127.0.0.1:5000/process_data', {
        keyword,
        city,
      }, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      const parsedData = JSON.parse(response.data);
      setTableData(parsedData);
      setIsLoading(false);
    } catch (error) {
      console.error('There was an error!', error);
      setIsLoading(false);
    }
  };

  const exportToExcel = () => {
    const wb = XLSX.utils.table_to_book(tableRef.current);  // Using table ref here
    XLSX.writeFile(wb, "SheetJS.xlsx");
  };

  return (
    <div className="App d-flex flex-column align-items-center justify-content-center">
      <form onSubmit={handleSubmit} className="mb-4">
        <div className="mb-3">
          <input
            type="text"
            className="form-control rounded"
            placeholder="Keyword"
            value={keyword}
            onChange={(e) => setKeyword(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <input
            type="text"
            className="form-control rounded"
            placeholder="City"
            value={city}
            onChange={(e) => setCity(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary btn-rounded">Search</button>
      </form>

      {isLoading && <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>}

      {tableData && !isLoading && (
        <div className="table-container">
          <button onClick={exportToExcel} className="btn btn-success mb-2">Export to Excel</button>
          <table className="table table-bordered">
            <thead>
              <tr>
                {tableData.columns.map((col, index) => (
                  <th key={index}>{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {tableData.data.map((row, index) => (
                <tr key={index}>
                  {row.map((cell, i) => (
                    <td key={i}>{cell}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
