import React, { useState } from 'react';
import axios from 'axios';
import * as XLSX from 'xlsx';
import './App.css'; // Importing CSS

function App() {
  const [keyword, setKeyword] = useState('');
  const [city, setCity] = useState('');
  const [tableData, setTableData] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Show loading screen
    setTableData(null);

    try {
      const response = await axios.post('http://127.0.0.1:5000/process_data', {
        keyword,
        city,
      });
      const parsedData = JSON.parse(response.data);
      setTableData(parsedData);
    } catch (error) {
      console.error('There was an error!', error);
    } finally {
      setLoading(false); // Hide loading screen
    }
  };

  const exportToExcel = () => {
    // ... your existing code
  };

  return (
    <div className="App container text-center">
      {loading && <div className="loading">Loading...</div>}
      <form onSubmit={handleSubmit} className="form-inline justify-content-center mt-5">
        <input
          type="text"
          className="form-control rounded mr-2"
          placeholder="Keyword"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
        />
        <input
          type="text"
          className="form-control rounded mr-2"
          placeholder="City"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button type="submit" className="btn btn-primary rounded">Search</button>
      </form>
      {tableData && (
        <div className="mt-5">
          <button onClick={exportToExcel} className="btn btn-success mb-3 rounded">Export to Excel</button>
          <div className="table-wrapper">
            <table className="table table-bordered">
              {/* ... rest of your table */}
            </table>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
