import React, { useState } from 'react';
import axios from 'axios';
import * as XLSX from 'xlsx';

function App() {
  const [keyword, setKeyword] = useState('');
  const [city, setCity] = useState('');
  const [tableData, setTableData] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:5000/process_data', {
        keyword,
        city,
      }, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      const parsedData = JSON.parse(response.data)
      setTableData(parsedData);
    } catch (error) {
      console.error('There was an error!', error);
    }
  };

  const exportToExcel = () => {

    const formattedData = tableData.data.map((row) => {
      const obj = {};
      tableData.columns.forEach((col, index) => {
        obj[col] = row[index];
      });
      return obj;
    });

    console.log(Array.isArray(formattedData))
    const ws = XLSX.utils.json_to_sheet(tableData);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Sheet1");
    XLSX.writeFile(wb, "SheetJS.xlsx");
  }

  const generateTableHead = (data) => {
    const columns = Object.keys(data);
    return columns.map((col, index) => <th key={index}>{col}</th>);
  };

  const generateTableRows = (data) => {
    const rows = Object.keys(data[Object.keys(data)[0]]);
    return rows.map((row, index) => (
      <tr key={index}>
        {Object.keys(data).map((col, i) => <td key={i}>{data[col][row]}</td>)}
      </tr>
    ));
  };

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <label>
          Keyword:
          <input type="text" value={keyword} onChange={(e) => setKeyword(e.target.value)} />
        </label>
        <label>
          City:
          <input type="text" value={city} onChange={(e) => setCity(e.target.value)} />
        </label>
        <button type="submit">Search</button>
      </form>
      {tableData && (
        <div>
          <button onClick={exportToExcel}>Export to Excel</button>
          <table border="1">
            <thead>
              <tr>
                {tableData.columns.map((col, index) => <th key={index}>{col}</th>)}
              </tr>
            </thead>
            <tbody>
              {tableData.data.map((row, index) => (
                <tr key={index}>
                  {row.map((cell, i) => <td key={i}>{cell}</td>)}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
